import { Component } from '@angular/core';
import { NavigationExtras, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage {

  username: string = '';
  password: string = '';
  message: string = '';

  constructor(private router: Router) {}

  validateLogin() {
   
    const usernameRegex = /^[A-Za-z0-9]{3,8}$/;
    if (!usernameRegex.test(this.username)) {
      this.message = 'El nombre de usuario debe tener entre 3 y 8 caracteres alfanuméricos.';
      return;
    }

    
    const passwordRegex = /^\d{4}$/;
    if (!passwordRegex.test(this.password)) {
      this.message = 'La contraseña debe ser un número de 4 dígitos.';
      return;
    }

    
    if (this.username === 'admin' && this.password === '1234') {
      let extras: NavigationExtras = {
        state: { user: this.username }
      }
      this.router.navigate(['/home'], extras);
    } else {
      this.message = 'Login con error';
    }
    
    
    console.log('Username:', this.username);
    console.log('Password:', this.password);
  }
}
<ion-header [translucent]="true" class="ion-no-border">
  <ion-toolbar>
    <ion-title class="ion-text-center">
      <h1>SkeletonAPP</h1>
      <h6>subtitulo</h6>
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Login</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-list>
    <ion-item>
      <ion-label position="floating">Username</ion-label>
      <ion-input
        [(ngModel)]="username"
        name="username"
        type="text"
        maxlength="8"
        minlength="3"
        pattern="[A-Za-z0-9]+"
        required
      ></ion-input>
    </ion-item>

    <ion-item>
      <ion-label position="floating">Password</ion-label>
      <ion-input
        [(ngModel)]="password"
        name="password"
        type="password"
        maxlength="4"
        minlength="4"
        pattern="\d{4}"
        required
      ></ion-input>
    </ion-item>
  </ion-list>

  <ion-button expand="block" (click)="validateLogin()">Ingresa</ion-button>

  <ion-list>
    <ion-item *ngIf="message">
      {{ message }}
    </ion-item>
    <ion-item>
      <ion-button [routerLink]="['/home']">Ir a Home</ion-button>
    </ion-item>
    <ion-item>
      <ion-button [routerLink]="['/profile']">Ir a Profile</ion-button>
    </ion-item>
  </ion-list>
</ion-content>
